transformations.script:
TripData, TripFare join(hack_license == { hack_license}
	&& TripData@medallion == TripFare@medallion
	&& vendor_id == { vendor_id}
	&& pickup_datetime == { pickup_datetime},
	joinType:'inner',
	broadcast: 'none')~> JoinMatchedData

CreateFullName select(mapColumn(
		phone = {Phone No. },
		zip = Zip,
		fullname,
		acctnum = {Emp ID}
	))~> MapNames
